package basicjava;

public class Javadatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		Float b = 1.1f;
		String c = "Swapnil";
		Character d = 'P';
		Boolean e = true;
		Double f = 3.14;
		
		
		System.out.println("The integer value is"+a);
		System.out.println("The float value is"+b);
		System.out.println("String value is"+c);
		System.out.println("Character value is"+d);
		System.out.println("Boolean value is"+e);
		System.out.println("Double value is"+f);
	}

}
